// --- On r√©cup√®re toutes les informations n√©cessaires depuis les noeuds pr√©c√©dents ---

// 1. Infos sur le GMM (module et classe)
// Assurez-vous que le nom du noeud "required_parameters_struct" est correct.
const gmm_data = $('Code3').first().json;
const module_name = gmm_data.module;
const class_name = gmm_data.class_name;
const requires = gmm_data.requires;

// 2. On reconstruit la cha√Æne de cl√©s
const keys_array = [...requires.rupture, ...requires.distance, ...requires.site];
const keys_string = keys_array.join(',');

// 3. On r√©cup√®re la r√©ponse de l'utilisateur
// Assurez-vous que le nom du noeud "Send message and wait for response1" est correct.
const user_response_data = $('Send message and wait for response1').first().json.data;
// On r√©cup√®re dynamiquement la cha√Æne de valeurs
const values_string = Object.values(user_response_data)[0];

// --- On construit la commande finale, √©tape par √©tape ---

// On commence avec la base de la commande
let command = "python3 /app/oq-wrappers/run_gmm_calculation_v8.py";

// On ajoute chaque argument l'un apr√®s l'autre
command += ` --module ${module_name}`;
command += ` --class_name ${class_name}`;
command += ` --keys "${keys_string}"`;
command += ` --values "${values_string}"`;

// --- On retourne la commande compl√®te ---

return [
  {
    json: {
      final_command: command
    }
  }
];

// =======================================================================
// N≈íUD Code1 : Agr√©gateur de Rapports (Avec Uniquement la Logique de R√©sum√©)
// =======================================================================

// --- √âTAPE 1: R√âCUP√âRER LES DONN√âES DE MANI√àRE S√âCURIS√âE ---

const calcData = $('Run Final Calculation1').first()?.json;
const interpretationText = $input.first()?.json.response || "";

if (!calcData || !calcData.stdout) {
  throw new Error("Les donn√©es du noeud 'Run Final Calculation1' sont manquantes ou invalides.");
}
const result = JSON.parse(calcData.stdout);

// --- √âTAPE 2: CONSTRUIRE LE RAPPORT TEXTUEL DE MANI√àRE D√âFENSIVE ---
// Le formatage initial (sans Markdown) est conserv√©.
let message = `üèóÔ∏è GMM_class Calculation: ${result.gmm || 'N/A'}\n\n`;
message += `üìä Abstract:\n`;
message += `‚Ä¢ Module: ${result.module || 'N/A'}\n`;

// R√©cup√©ration des Physical Parameters (logique inchang√©e)
try {
  const finalCommandData = $('Prepare Final Command1').first()?.json?.final_command;
  if (finalCommandData && typeof finalCommandData === 'string') {
    message += '‚Ä¢ Physical Parameters:\n';
    const keysMatch = finalCommandData.match(/--keys\s+"([^"]+)"/);
    const valuesMatch = finalCommandData.match(/--values\s+"([^"]+)"/);
    if (keysMatch && valuesMatch) {
      const keys = keysMatch[1].split(',').map(k => k.trim());
      const values = valuesMatch[1].split(',').map(v => v.trim());
      if (keys.length === values.length) {
        for (let i = 0; i < keys.length; i++) {
          message += `  - ${keys[i]} = ${values[i]}\n`;
        }
      }
    }
  }
} catch (e) {
  message += '‚Ä¢ Physical Parameters: (error parsing final_command)\n';
}

message += `‚Ä¢ Numbers of IMs supported by model: ${result.successful_imts_count || 0}/${result.total_imts_tested || 0}\n`;

const imtResults = result.imt_results || [];

// =======================================================================
// === MODIFICATION : LOGIQUE DE R√âSUM√â INTELLIGENT APPLIQU√âE ICI ===
// =======================================================================

// 1. D'abord, on s√©pare les IMTs scalaires des IMTs spectraux
const spectralTypes = ['SA', 'SDi', 'EAS', 'FAS', 'DRVT', 'AvgSA', 'VHR_SA'];
const scalarImts = imtResults.filter(r => !spectralTypes.includes(r.imt) && r.success);

// On affiche les IMTs scalaires (PGA, PGV, etc.), qui sont toujours peu nombreux.
if (scalarImts.length > 0) {
    message += `\nüìà Base IMTs (successful):\n`;
    scalarImts.forEach(imt => {
        const meanValue = parseFloat(imt.mean_value).toFixed(4);
        const stdDev = parseFloat(imt.stddev_total_ln).toFixed(4);
        const unit = imt.unit || '';
        message += `‚Ä¢ ${imt.display_name}: ${meanValue}${unit} (œÉ=${stdDev})\n`;
    });
}

// 2. Ensuite, on cr√©e un r√©sum√© pour le PREMIER type spectral trouv√©
let summaryCreated = false;
for (const type of spectralTypes) {
    const spectralResults = imtResults.filter(r => r.imt === type && r.success);

    if (spectralResults.length > 0 && !summaryCreated) {
        message += `\nüåä ${type} Spectrum Summary (Sampled Values):\n`;

        const sortKey = ['SA', 'SDi', 'AvgSA', 'VHR_SA'].includes(type) ? 'sa_period' : 'frequency';
        spectralResults.sort((a, b) => a[sortKey] - b[sortKey]);

        let sampledResults = [];
        const count = spectralResults.length;

        if (count <= 9) {
            // Si on a 9 valeurs ou moins, on les prend toutes
            sampledResults = spectralResults;
        } else {
            // Sinon, on prend les 3 premi√®res, les 3 du milieu, et les 3 derni√®res
            const middleIndex = Math.floor(count / 2) - 1;
            sampledResults = [
                ...spectralResults.slice(0, 3),
                ...spectralResults.slice(middleIndex, middleIndex + 3),
                ...spectralResults.slice(-3)
            ];
            // On s'assure qu'il n'y a pas de doublons
            sampledResults = [...new Map(sampledResults.map(item => [item.display_name, item])).values()];
        }

        sampledResults.forEach(item => {
            const meanValue = parseFloat(item.mean_value).toFixed(5);
            const stdDev = parseFloat(item.stddev_total_ln).toFixed(4);
            const unit = item.unit || '';
            message += `‚Ä¢ ${item.display_name}: ${meanValue}${unit} (œÉ=${stdDev})\n`;
        });

        summaryCreated = true; // On a cr√©√© un r√©sum√©, on arr√™te la boucle.
    }
}
// =======================================================================
// === FIN DE LA MODIFICATION ===
// =======================================================================

message += '\n';

if (result.failed_imts_count > 0) {
    message += `‚ö†Ô∏è ${result.failed_imts_count} IMTs not supported.\n\n`;
}

// --- √âTAPE 3: RETOURNER TOUTES LES DONN√âES AGR√âG√âES ---
return [{
    json: {
        final_report: message,
        interpretation_text: interpretationText,
        has_plot: !!result.plot_path,
        plot_path: result.plot_path || null,
        sa_count: imtResults.filter(r => r.imt === 'SA').length,
        sdi_count: imtResults.filter(r => r.imt === 'SDi').length
    }
}];

// =======================================================================
// N≈íUD Code2 : Formate la PREMI√àRE partie de la liste des GMMs (1-150)
// =======================================================================

// --- √âTAPE 1: R√âCUP√âRER LA LISTE FILTR√âE ---
// On lit la sortie du n≈ìud 'Code22' qui contient la VRAIE liste des items filtr√©s.
// !! Assurez-vous que le nom 'Code22' est correct !!
const allFilteredItems = $('Code22').all();

// --- √âTAPE 2: S√âLECTIONNER LA PREMI√àRE PARTIE (les 150 premiers) ---
// .slice(0, 150) prend les √©l√©ments de l'index 0 jusqu'√† 149 (soit 150 √©l√©ments).
const firstBatch = allFilteredItems.slice(0, 100);

// --- √âTAPE 3: FORMATER LE MESSAGE ---
const title = `*Here is the list of GMMs available in OpenQuake *\n`;
const messageLines = [];

// Expression r√©guli√®re pour "√©chapper" les caract√®res sp√©ciaux de Markdown V2
const specialChars = /[_*[\]()~`>#+\-=|{}.!]/g;

// On boucle sur la PREMI√àRE PARTIE de la liste (les 150 items)
firstBatch.forEach((item, index) => {
  // Le num√©ro de la ligne doit √™tre l'index + 1
  const newRowNumber = index + 1;

  // On r√©cup√®re le nom de la classe et on √©chappe les caract√®res sp√©ciaux
  const className = (item.json.class_name || 'Unknown GMM').replace(specialChars, '\\$&');

  // On ajoute la ligne format√©e au tableau
  messageLines.push(`${newRowNumber}) ${className}`);
});

// On assemble le titre et les lignes du message.
const finalMessage = title + '\n' + messageLines.join('\n');

// --- √âTAPE 4: RETOURNER LE PREMIER MESSAGE ---
// On retourne un seul item, contenant le message complet.
return [{
  json: {
    telegramMessage: finalMessage
  }
}];

// --- CE SCRIPT FAIT TROIS CHOSES ---
// 1. Il r√©cup√®re la liste filtr√©e que l'utilisateur a vue (depuis Code22).
// 2. Il utilise le num√©ro choisi par l'utilisateur pour trouver le VRAI nom de la classe.
// 3. Il cherche ce nom dans la feuille de calcul compl√®te pour extraire et formater les donn√©es.

// --- √âTAPE 1: R√©cup√©rer toutes les informations n√©cessaires ---

// R√©cup√®re la LISTE FILTR√âE des items depuis le n≈ìud qui l'a cr√©√©e.
// !! IMPORTANT !! Assurez-vous que le nom du n≈ìud est bien 'Code22'.
const filteredItemsFromCode22 = $('Code22').all();

// R√©cup√®re TOUS les items (519 lignes) du noeud Google Sheet.
const allModules = $('Get row(s) in sheet3').all();

// R√©cup√®re le NUM√âRO que l'utilisateur a choisi (ex: "1", "2", etc.).
const choiceNodeOutput = $('Choice of GMM').first().json;
const selectedNumber = parseInt(choiceNodeOutput.data.text, 10);

// V√©rifications de s√©curit√©.
if (isNaN(selectedNumber)) {
    return [{ json: { error: "Impossible de lire le num√©ro du module choisi." } }];
}
if (!filteredItemsFromCode22 || filteredItemsFromCode22.length === 0) {
    return [{ json: { error: "Impossible de r√©cup√©rer la liste filtr√©e depuis le n≈ìud 'Code22'." } }];
}

// --- √âTAPE 2: Trouver le nom de la classe correspondant au num√©ro choisi ---

const selectedIndex = selectedNumber - 1;

// Autre v√©rification : le num√©ro est-il valide par rapport √† la taille de la liste ?
if (selectedIndex < 0 || selectedIndex >= filteredItemsFromCode22.length) {
    return [{ json: { error: `Le num√©ro choisi '${selectedNumber}' est invalide pour la liste de ${filteredItemsFromCode22.length} options.` } }];
}

// On extrait le nom de la classe de la liste filtr√©e.
const selectedClassName = filteredItemsFromCode22[selectedIndex].json.class_name;


// --- √âTAPE 3: Trouver la ligne compl√®te dans la feuille de calcul ---

const selectedModuleItem = allModules.find(item => item.json.class_name === selectedClassName);

if (!selectedModuleItem) {
    return [{ json: { error: `Impossible de trouver les d√©tails pour la classe nomm√©e "${selectedClassName}" dans la feuille de calcul.` } }];
}

const selectedModuleData = selectedModuleItem.json;


// --- √âTAPE 4: Formater la sortie en utilisant votre logique ---

// Cr√©e le nouvel objet JSON structur√© en sortie.
const structuredOutput = {
  module: selectedModuleData.module,
  class_name: selectedModuleData.class_name,
  requires: {
    site: [],
    rupture: [],
    distance: []
  },
  supported_imts: []
};

// Peuple les listes de param√®tres requis ET des IMTs.
for (const key in selectedModuleData) {
  const value = selectedModuleData[key];

  if (value && typeof value === 'string' && value.trim() !== "") {
    if (key.startsWith('site.')) {
      structuredOutput.requires.site.push(value.trim());
    } else if (key.startsWith('rupture.')) {
      structuredOutput.requires.rupture.push(value.trim());
    } else if (key.startsWith('distance.')) {
      structuredOutput.requires.distance.push(value.trim());
    } else if (key.startsWith('supported_imts.')) {
      structuredOutput.supported_imts.push(value.trim());
    }
  }
}

// Cr√©e une liste simple de tous les param√®tres requis.
const requiredParamsList = [
    ...structuredOutput.requires.site, 
    ...structuredOutput.requires.rupture, 
    ...structuredOutput.requires.distance
];

// Stocke le nombre de param√®tres attendus.
const expectedParamCount = requiredParamsList.length;

// Cr√©e le message √† envoyer √† l'utilisateur.
const promptForUser = `
Please enter the ${expectedParamCount} physical parameter values according to the following format:
**${requiredParamsList.join(',')}**

Reply to this message with the values separated by commas (e.g., 760,10,6.5,50).
`;

// Ajoute ces nouvelles informations √† notre objet de sortie.
structuredOutput.required_params_list = requiredParamsList;
structuredOutput.expected_param_count = expectedParamCount;
structuredOutput.prompt_for_user = promptForUser.trim();

// Retourne l'objet JSON unique et bien structur√©.
return [{ 
  json: structuredOutput 
}];

