// --- Input attendu : {{$json.text}}  (string) ---
const txt =  $('Send a text message3').first().json.result.text  // <- utilise la vraie entr√©e

const extract = (block) => {
  const regex = new RegExp(`\\*${block} Parameters\\*:\\n([\\s\\S]*?)(?=\\n\\*|$)`, 'i');
  const m = txt.match(regex);
  if (!m) return [];
  return m[1]
    .split('\n')
    .map(l => l.match(/:\s*([^\s`]+)/)?.[1])
    .filter(Boolean);
};

const out = [
  ...extract('Rupture'),
  ...extract('Distance'),
  ...extract('Site')
].join(', ');

return { required: out };   // ‚Üê objet minimal attendu par le workflow

// Grab the documentation string
const doc = $input.first().json['info.documentation'] || '';

// Return it in the required array-of-objects form
return [{ json: { info_documentation: doc } }];

// =======================================================================
// N≈íUD Code5 : Constructeur de la page HTML (Version Finale et Compl√®te)
// =======================================================================

const fs = require('fs');

// --- CONFIGURATION DE L'ENT√äTE ---
const AUTHOR_NAME = "Pr. Boum√©di√®ne DERRAS"; 
const PROJECT_TITLE = "Advanced Seismic Analysis Project";

// --- PARTIE 1: R√âCUP√âRATION DE TOUTES LES DONN√âES ---
const mergedData = $input.first().json;

// On r√©cup√®re chaque pi√®ce du puzzle depuis les donn√©es d'entr√©e
const reportText = mergedData.final_report;
const interpretationText = mergedData.interpretation_text;
const sourceImagePath = mergedData.plot_path;
const definitionsHtml = mergedData.formatted_text; 

// On va chercher la description du mod√®le depuis le noeud Code4
const documentationData = $('Code4').first()?.json;
const modelDescriptionRaw = documentationData?.info_documentation || 'No model description provided.';

// --- CORRECTION : GESTION INTELLIGENTE DES PARAGRAPHES POUR LA DESCRIPTION ---
// 1. On divise le texte en blocs √† chaque double saut de ligne
const paragraphs = modelDescriptionRaw.split(/\n\s*\n/);
// 2. On enveloppe chaque bloc dans une balise <p> et on remplace les sauts de ligne simples par un espace
const modelDescription = paragraphs.map(p => `<p class="description">${p.replace(/\n/g, ' ')}</p>`).join('');


// --- PARTIE 2: GESTION DE L'IMAGE ---
// const finalImageName = `response_spectrum_${Date.now()}.png`;
const finalImageName = `response_spectrum.png`;
const destinationImagePath = `/app/oq-wrappers/${finalImageName}`;
let imageHtml = ''; 

if (sourceImagePath) {
  try {
    fs.copyFileSync(sourceImagePath, destinationImagePath);
    imageHtml = `<img src="${finalImageName}" alt="Response Spectrum Plot" style="max-width: 100%; height: auto; margin-top: 20px;" />`;
  } catch (error) {
    console.log(`Error copying image: ${error.message}`);
    imageHtml = '<p><em>Error: The plot could not be loaded.</em></p>';
  }
} else {
    imageHtml = '<p><em>(No plot was generated for this calculation.)</em></p>';
}

// --- PARTIE 3: CONSTRUCTION DU HTML FINAL ---
const currentDate = new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' });
const aiCreditText1 = `<p class="ai-credit">This interpretation was generated by a Gemini 2.0 Flash AI agent and should be reviewed by a qualified expert.</p>`;
const aiCreditText2 = `<p class="ai-credit">This definitions was generated by a Gemini 2.0 Flash AI agent and should be reviewed by a qualified expert.</p>`;

const finalHtml = `
<html>
  <head>
    <meta charset="utf-8">
    <title>Seismic Analysis Report</title>
    <style>
      body { font-family: Helvetica, Arial, sans-serif; font-size: 11pt; line-height: 1.5; max-width: 800px; margin: auto; color: #333; }
      .header { display: flex; justify-content: space-between; align-items: flex-start; border-bottom: 2px solid #004a99; padding-bottom: 20px; margin-bottom: 40px; }
      .header-left { max-width: 75%; }
      .header h1 { margin: 0; font-size: 24pt; color: #004a99; }
      .header .subtitle { margin: 5px 0 0 0; font-size: 14pt; color: #555; font-weight: normal; }
      .header .author-info { margin-top: 20px; font-size: 10pt; color: #666; }
      .header-right { text-align: right; }
      .logo { max-height: 80px; }
      h2 { font-size: 16pt; color: #444; margin-top: 30px; border-bottom: 1px solid #ccc; padding-bottom: 5px; page-break-after: avoid; }

      /* Style pour tous les paragraphes de texte justifi√©s */
      p.description, div.description, div.description_definitions { 
        text-align: justify; 
        color: #555; 
      }

      pre { white-space: pre-wrap; word-wrap: break-word; font-family: 'Courier New', monospace; font-size: 9pt; background-color: #f4f4f4; padding: 15px; border: 1px solid #ddd; border-radius: 5px; }
      .appendix { page-break-before: always; }
      .ai-credit { text-align: right; font-size: 8pt; font-style: italic; color: #999; margin-top: 15px; }
      @page { size: A4; margin: 2.5cm; @bottom-center { content: "Page " counter(page) " of " counter(pages); font-family: Helvetica, Arial, sans-serif; font-size: 9pt; color: #888; } }
    </style>
  </head>
  <body>

    <header class="header">
      <div class="header-left">
        <h1>Seismic Analysis Report</h1>
        <p class="subtitle">${PROJECT_TITLE}</p>
        <p class="author-info">Prepared by: ${AUTHOR_NAME}<br/>Date: ${currentDate}</p>
      </div>
      <div class="header-right">
        <img src="/app/oq-wrappers/logo.png" alt="Logo" class="logo" />
      </div>
    </header>

    <h2>Model Description</h2>
    ${modelDescription}

    <h2>Textual Calculation Results</h2>
    <pre>${reportText}</pre>

    <h2>Response Spectrum Plot</h2>
    ${imageHtml}

    ${interpretationText ? `
      <h2>AI-Powered Interpretation</h2>
      <div class="description">${interpretationText.replace(/\n/g, '<br>')}</div>
      ${aiCreditText1}
    ` : ''}

    <div class="appendix">
        <h2>Appendix: Term Definitions</h2>
        <div class="description_definitions">${definitionsHtml.replace(/\n/g, '<br>')}</div>
        ${aiCreditText2}
    </div>

  </body>
</html>
`;

// --- PARTIE 4: RETOUR ---
return [{
  json: {
    ...mergedData,
    report_html: finalHtml
  }
}];

const fs = require('fs');

// ON VA CHERCHER LE CONTENU HTML DIRECTEMENT DANS LE NOEUD "Code5"
const htmlContent = $('Code5').first().json.report_html;

// =======================================================================
// MODIFICATION : D√©finir les chemins de fichiers FIXES
// On a supprim√© la variable "timestamp" pour que les noms soient toujours les m√™mes.
// =======================================================================
const tempHtmlPath = '/app/oq-wrappers/temp_report.html';
const finalPdfPath = '/app/oq-wrappers/final_report.pdf';

//const timestamp = Date.now();
//const tempHtmlPath = `/app/oq-wrappers/temp_report_${timestamp}.html`;
//const finalPdfPath = `/app/oq-wrappers/final_report_${timestamp}.pdf`;

// On v√©rifie que le contenu n'est pas vide avant d'√©crire
if (htmlContent) {
  // fs.writeFileSync() va automatiquement √©craser le fichier temp_report.html pr√©c√©dent.
  fs.writeFileSync(tempHtmlPath, htmlContent);
}

// On retourne les chemins de fichiers fixes pour que les noeuds suivants puissent les utiliser
return [{
  json: {
    ...$input.first().json, // On passe les donn√©es d'entr√©e (de l'IA) au noeud suivant
    temp_html_path: tempHtmlPath,
    final_pdf_path: finalPdfPath 
  }
}];

// On a besoin du module 'fs' (file-system) pour lire le fichier depuis le disque
const fs = require('fs');

// --- √âTAPE 1: R√©cup√©rer le chemin du fichier depuis l'entr√©e ---
// On r√©cup√®re le chemin complet du fichier (ex: /tmp/sdi_spectrum.png)
const filePath = $input.first().json.plot_path;

// S√©curit√© : On v√©rifie que le chemin existe avant de continuer
if (!filePath) {
  throw new Error("La propri√©t√© 'plot_path' est manquante dans les donn√©es d'entr√©e.");
}

// --- √âTAPE 2: Extraire le nom du fichier (la partie dynamique) ---
// On coupe la cha√Æne de caract√®res √† chaque '/' et on prend le dernier √©l√©ment.
// C'est la m√©thode la plus simple pour extraire un nom de fichier d'un chemin.
const fileName = filePath.split('/').pop();

// --- √âTAPE 3: Lire le fichier et le retourner en tant que donn√©e binaire ---
try {
  // Lit le contenu du fichier image en tant que buffer binaire
  const fileBuffer = fs.readFileSync(filePath);

  // On retourne les donn√©es. L'objet 'binary' est ce que n8n utilise pour les fichiers.
  return [{
    json: {
      ...$input.first().json, // On conserve les donn√©es d'entr√©e (final_report, etc.)
      file_size: fileBuffer.length // On ajoute la taille du fichier pour information
    },
    binary: {
      // Le nom de la propri√©t√© ici ('photo') est celui que vous utiliserez dans les noeuds suivants
      photo: {
        data: fileBuffer,
        mimeType: 'image/png',
        // MODIFICATION : On utilise notre variable dynamique ici !
        fileName: fileName
      }
    }
  }];

} catch (error) {
  // Si le fichier n'existe pas ou ne peut pas √™tre lu, on g√©n√®re une erreur claire.
  throw new Error(`Impossible de lire le fichier ${filePath}: ${error.message}`);
}

// =======================================================================
// N≈íUD "Assemble Report" (le noeud Code apr√®s AI Agent)
// =======================================================================
// --- √âTAPE 1: R√âCUP√âRER LES DONN√âES AUX BONNES SOURCES ---
// On r√©cup√®re les r√©sultats du calcul DEPUIS LE NOEUD Code7
const calculationResult = $('Code7').first().json;
// On r√©cup√®re l'interpr√©tation de l'IA DEPUIS L'ENTR√âE DIRECTE de ce noeud
const interpretationResult = $input.first().json.candidates[0].content.parts[0].text;
// --- √âTAPE 2: EXTRAIRE LES INFORMATIONS UTILES ---
// On s'assure que les variables existent, sinon on met une valeur par d√©faut
const reportText = calculationResult.final_report || "Calculation report not found.";
const plotPath = calculationResult.plot_path || null;
