      // V√©rifier que nous avons le m√™me nombre de cl√©s et de valeurs
      if (keys.length === values.length) {
        for (let i = 0; i < keys.length; i++) {
          message += `  - ${keys[i]} = ${values[i]}\n`;
        }
      } else {
        // Si mismatch, afficher s√©par√©ment
        message += `  - Keys: ${keys.join(', ')}\n`;
        message += `  - Values: ${values.join(', ')}\n`;
        console.warn("Mismatch entre le nombre de keys et values");
      }
    } else {
      // Fallback: essayer d'extraire d'autres param√®tres de la commande
      message += '  - Command parameters:\n';

      // Extraire le module
      const moduleMatch = finalCommandData.match(/--module\s+(\S+)/);
      if (moduleMatch) {
        message += `    - module = ${moduleMatch[1]}\n`;
      }

      // Extraire le class_name
      const classMatch = finalCommandData.match(/--class_name\s+(\S+)/);
      if (classMatch) {
        message += `    - class_name = ${classMatch[1]}\n`;
      }

      // Si on n'a pas trouv√© keys/values, les chercher sans guillemets
      const keysMatchAlt = finalCommandData.match(/--keys\s+(\S+)/);
      const valuesMatchAlt = finalCommandData.match(/--values\s+(\S+)/);

      if (keysMatchAlt && valuesMatchAlt) {
        const keys = keysMatchAlt[1].replace(/"/g, '').split(',').map(k => k.trim());
        const values = valuesMatchAlt[1].replace(/"/g, '').split(',').map(v => v.trim());

        if (keys.length === values.length) {
          for (let i = 0; i < keys.length; i++) {
            message += `    - ${keys[i]} = ${values[i]}\n`;
          }
        }
      }
    }
  } else {
    message += '‚Ä¢ Physical Parameters: (final_command not found or not a string)\n';
    console.log("Structure du n≈ìud:", $('Prepare Final Command1').first()?.json);
  }
} catch (e) {
  message += '‚Ä¢ Physical Parameters: (error parsing final_command)\n';
