// Cette fonction s√©lectionne les donn√©es spectrales les plus pertinentes
function getSpectralSummary(spectralResults, type) {
    if (!spectralResults || spectralResults.length === 0) return "";

    let summary = `\nüåä ${type} Spectrum Summary (Sampled Values):\n`;
    const periodKey = type === 'SA' ? 'sa_period' : 'sdi_period';
    const unit = type === 'SA' ? 'g' : 'cm';

    // P√©riodes cl√©s √† extraire (courtes, interm√©diaires, longues)
    const key_periods = [0.1, 0.2, 0.5, 1.0, 2.0, 5.0];
    let sampled_results = [];

    // Trier les r√©sultats par p√©riode
    spectralResults.sort((a, b) => a[periodKey] - b[periodKey]);

    // Pour chaque p√©riode cl√©, trouver le r√©sultat le plus proche
    for (const period of key_periods) {
        const closest = spectralResults.reduce((prev, curr) => 
            Math.abs(curr[periodKey] - period) < Math.abs(prev[periodKey] - period) ? curr : prev
        );
        sampled_results.push(closest);
    }

    // √âliminer les doublons et s'assurer qu'on n'a pas trop de lignes
    const unique_results = Array.from(new Map(sampled_results.map(item => [item.display_name, item])).values()).slice(0, 6);

   for (const item of unique_results) {
        summary += `  ${item.display_name}: ${parseFloat(item.mean_value).toFixed(4)}${unit} (œÉ=${item.stddev_total_ln})\n`;
    }
    return summary;
}

const saResults = imtResults.filter(r => r.imt === 'SA' && r.success);
const sdiResults = imtResults.filter(r => r.imt === 'SDi' && r.success);

if (saResults.length > 0) {
    message += getSpectralSummary(saResults, 'SA');
} else if (sdiResults.length > 0) {
    message += getSpectralSummary(sdiResults, 'SDi');
}

message += '\n';

if (result.failed_imts_count > 0) {
    message += `‚ö†Ô∏è ${result.failed_imts_count} IMTs not supported.\n\n`;
}

